const config = $('Config').item.json;
const items = $input.all();
const cacheKey = 'newsletter_seen_urls';

// Get existing cache
let seenUrls = $getWorkflowStaticData('global');
if (!seenUrls[cacheKey]) {
  seenUrls[cacheKey] = [];
}

// Filter out already seen items
const newItems = [];
for (const item of items) {
  const url = item.json.link || item.json.guid;
  if (!seenUrls[cacheKey].includes(url)) {
    newItems.push(item);
    if (!config.dryRun) {
      seenUrls[cacheKey].push(url);
    }
  }
}

// Keep cache size manageable
if (seenUrls[cacheKey].length > 1000) {
  seenUrls[cacheKey] = seenUrls[cacheKey].slice(-500);
}

// Limit to maxItems
const limitedItems = newItems.slice(0, config.maxItems);

return limitedItems.map(item => ({
  json: {
    ...item.json,
    stats: {
      totalSeen: items.length,
      newItems: newItems.length,
      kept: limitedItems.length,
      skipped: items.length - limitedItems.length
    }
  }
}));

// Simulate AI curation (in production, this would call Claude API)
const item = $input.item.json;

// Extract key info and score relevance
const title = item.title || '';
const description = item.description || item.summary || '';
const content = item.content || item['content:encoded'] || description;

// Simple scoring based on keywords
const aiKeywords = ['AI', 'machine learning', 'neural', 'GPT', 'LLM', 'automation', 'robotics'];
let relevanceScore = 0;

for (const keyword of aiKeywords) {
  if (title.toLowerCase().includes(keyword.toLowerCase())) relevanceScore += 2;
  if (description.toLowerCase().includes(keyword.toLowerCase())) relevanceScore += 1;
}

// Generate summary (simulated)
const summary = description.length > 200 ? 
  description.substring(0, 197) + '...' : 
  description;

// Add curation metadata
return {
  json: {
    ...item,
    curated: {
      relevanceScore,
      summary,
      category: relevanceScore > 3 ? 'Featured' : 'Standard',
      readTime: Math.ceil(content.split(' ').length / 200) + ' min',
      curatedAt: new Date().toISOString()
    }
  }
};

const config = $('Config').item.json;
const items = $input.all();
const stats = items[0]?.json.stats || {};

// Group by category
const featured = items.filter(i => i.json.curated.category === 'Featured');
const standard = items.filter(i => i.json.curated.category === 'Standard');

// Format newsletter content
let newsletterHtml = `<h1>ðŸ¤– AI & Tech Innovation Daily</h1>`;
nesletterHtml += `<p><em>${new Date().toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</em></p>`;

if (featured.length > 0) {
  newsletterHtml += `<h2>ðŸŒŸ Featured Stories</h2>`;
  featured.forEach(item => {
    newsletterHtml += `
      <div style="margin-bottom: 20px;">
        <h3><a href="${item.json.link}">${item.json.title}</a></h3>
        <p>${item.json.curated.summary}</p>
        <p><small>ðŸ“– ${item.json.curated.readTime} read | Score: ${item.json.curated.relevanceScore}/10</small></p>
      </div>
    `;
  });
}

if (standard.length > 0) {
  newsletterHtml += `<h2>ðŸ“° More Stories</h2>`;
  standard.forEach(item => {
    newsletterHtml += `
      <div style="margin-bottom: 15px;">
        <h4><a href="${item.json.link}">${item.json.title}</a></h4>
        <p><small>${item.json.curated.summary.substring(0, 100)}...</small></p>
      </div>
    `;
  });
}

// Add footer
newsletterHtml += `
  <hr>
  <p><small>Curated by AI | ${items.length} stories selected from ${stats.totalSeen || 0} articles</small></p>
`;

return [{
  json: {
    newsletter: {
      html: newsletterHtml,
      subject: `AI Daily: ${featured.length} Featured Stories - ${new Date().toLocaleDateString()}`,
      previewText: featured[0]?.json.title || 'Latest AI innovations and insights',
      stats: {
        ...stats,
        featured: featured.length,
        standard: standard.length
      }
    },
    config
  }
}];

const data = $input.item.json;
const config = data.config;

if (config.dryRun) {
  // In dry run, just prepare preview
  return {
    json: {
      ...data,
      published: false,
      action: 'preview',
      message: 'Dry run - newsletter prepared but not sent',
      previewUrl: 'Newsletter would be published to subscribers'
    }
  };
} else {
  // In production, this would send to email service/blog
  return {
    json: {
      ...data,
      published: true,
      action: 'published',
      message: 'Newsletter published successfully',
      publishedAt: new Date().toISOString(),
      subscriberCount: 2500 // Simulated
    }
  };
}

// Save state only if not in dry run
const config = $('Config').item.json;

if (!config.dryRun) {
  const staticData = $getWorkflowStaticData('global');
  staticData.lastRun = new Date().toISOString();
  staticData.runCount = (staticData.runCount || 0) + 1;
}

return [$input.item];

