// Simplified data fetching with mock data for testing
const testMode = true;

if (testMode) {
  const mockData = [
    {
      label: 'notion template',
      signals: {
        reddit_posts: 20,
        reddit_questions: 8,
        reddit_upvotes: 1200,
        gum_count: 25,
        gum_avg_price: 15,
        trend_ratio: 1.2,
        pinterest_signal: 45
      }
    },
    {
      label: 'client onboarding',
      signals: {
        reddit_posts: 15,
        reddit_questions: 12,
        reddit_upvotes: 800,
        gum_count: 18,
        gum_avg_price: 22,
        trend_ratio: 1.4,
        pinterest_signal: 38
      }
    },
    {
      label: 'meal prep guide',
      signals: {
        reddit_posts: 18,
        reddit_questions: 6,
        reddit_upvotes: 950,
        gum_count: 32,
        gum_avg_price: 12,
        trend_ratio: 0.8,
        pinterest_signal: 52
      }
    }
  ];

  console.log('Using mock data for testing');
  return mockData.map(item => ({ json: item }));
}

return [{ json: { error: 'Real API calls not implemented yet' } }];

const items = $input.all();
console.log(`Processing ${items.length} items`);

const validItems = items.filter(item => item.json && !item.json.error);

if (validItems.length === 0) {
  console.log('No valid data to process');
  return [{
    json: {
      processed_niches: [],
      timestamp: new Date().toISOString(),
      status: 'no_data'
    }
  }];
}

console.log(`Found ${validItems.length} valid niches to process`);

const result = {
  processed_niches: validItems.map(item => item.json),
  timestamp: new Date().toISOString(),
  status: 'success'
};

return [{ json: result }];

const data = $input.item.json;

if (data.status !== 'success' || !data.processed_niches) {
  const errorResult = {
    report: {
      top_3: [],
      winner: null,
      error: 'No data available for scoring'
    },
    discord_message: '🎯 **Daily Niche Hunter Report**\n\n❌ No data available for analysis.',
    timestamp: new Date().toISOString()
  };
  return [{ json: errorResult }];
}

const niches = data.processed_niches;

// Score each niche
const scoredNiches = niches.map(niche => {
  const signals = niche.signals;

  const demand = Math.min(signals.reddit_questions / 5, 1) * 20 + Math.min(signals.reddit_upvotes / 500, 1) * 15;
  const monet = Math.min(signals.gum_count / 20, 1) * 10 + Math.min(signals.gum_avg_price / 40, 1) * 15;
  const comp = (1 - Math.min(signals.gum_count / 30, 1)) * 20;
  const trend = Math.min(signals.trend_ratio / 1.3, 1) * 15 + Math.min(signals.pinterest_signal / 50, 1) * 5;
  const total = Math.round(demand + monet + comp + trend);

  const go = total >= 65 && signals.gum_avg_price >= 9 && signals.trend_ratio >= 0.9 && signals.gum_count <= 45;

  return {
    label: niche.label,
    signals: signals,
    scores: {
      demand: Math.round(demand),
      monet: Math.round(monet),
      comp: Math.round(comp),
      trend: Math.round(trend),
      total: total
    },
    go: go
  };
});

// Sort by total score descending
scoredNiches.sort((a, b) => b.scores.total - a.scores.total);
const top3 = scoredNiches.slice(0, 3);

// Product ideas and pricing
const getProductIdea = (niche) => {
  const templates = [`Complete ${niche} Template Bundle`, `${niche} Checklist & Planning Kit`, `Ultimate ${niche} Guide with Templates`];
  return templates[Math.floor(Math.random() * templates.length)];
};

const suggestPrice = (scores) => {
  if (scores.total >= 80) return 29;
  if (scores.total >= 70) return 19;
  return 12;
};

// Create report
const report = {
  top_3: top3.map(item => ({
    niche: item.label,
    score: item.scores.total,
    product_idea: getProductIdea(item.label),
    price_usd: suggestPrice(item.scores),
    go: item.go
  })),
  winner: null
};

// Set winner
const winner = top3.find(item => item.go) || top3[0];
if (winner) {
  const baseKeywords = ['template', 'checklist', 'planner', 'guide', 'notion'];
  const nicheTokens = winner.label.toLowerCase().split(/[\s+]+/).filter(t => t.length > 2);
  const seedKeywords = [...new Set([...nicheTokens, ...baseKeywords])].slice(0, 8);

  report.winner = {
    niche: winner.label,
    seed_keywords: seedKeywords,
    score: winner.scores.total
  };
}

// Create Discord message
let message = `🎯 **Daily Niche Hunter Report** - ${new Date().toLocaleDateString()}\n\n**TOP 3 NICHES:**\n\n`;

top3.forEach((item, index) => {
  const emoji = item.go ? '✅ GO' : '⏸️ HOLD';
  message += `**${index + 1}. ${item.niche}** (${emoji})\n`;
  message += `   📊 Score: ${item.scores.total}/100\n`;
  message += `   💡 Product: ${getProductIdea(item.label)}\n`;
  message += `   💰 Price: $${suggestPrice(item.scores)}\n\n`;
});

if (report.winner) {
  message += `🏆 **WINNER:** ${report.winner.niche}\n`;
  message += `🔑 **Keywords:** ${report.winner.seed_keywords.join(', ')}\n\n`;
} else {
  message += `❌ **No winner** - No niches met Go criteria.\n\n`;
}

message += `*Analyzed ${niches.length} niches*`;

const finalResult = {
  report: report,
  discord_message: message,
  timestamp: data.timestamp,
  analyzed_count: niches.length
};

return [{ json: finalResult }];

