// Validate required input fields
const input = $input.item.json;

if (!input.niche || !input.seed_keywords) {
  throw new Error('Missing required fields: niche and seed_keywords');
}

if (!Array.isArray(input.seed_keywords) || input.seed_keywords.length === 0) {
  throw new Error('seed_keywords must be a non-empty array');
}

console.log(`Starting product creation for niche: ${input.niche}`);
console.log(`Keywords: ${input.seed_keywords.join(', ')}`);

// Clean and prepare data
const cleanNiche = input.niche.trim();
const keywords = input.seed_keywords.map(k => k.toString().trim()).filter(k => k.length > 0);

return [{
  json: {
    niche: cleanNiche,
    seed_keywords: keywords,
    timestamp: new Date().toISOString(),
    topic_title: cleanNiche.charAt(0).toUpperCase() + cleanNiche.slice(1)
  }
}];

// Convert markdown to HTML with styling
const outline = JSON.parse($('Generate Outline').item.json.choices[0].message.content);
const markdownContent = $input.item.json.choices[0].message.content;

// Simple markdown to HTML conversion
const convertMarkdown = (md) => {
  return md
    // Headers
    .replace(/^### (.*$)/gm, '<h3>$1</h3>')
    .replace(/^## (.*$)/gm, '<h2>$1</h2>')
    .replace(/^# (.*$)/gm, '<h1>$1</h1>')
    // Bold and italic
    .replace(/\*\*\*(.*?)\*\*\*/g, '<strong><em>$1</em></strong>')
    .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
    .replace(/\*(.*?)\*/g, '<em>$1</em>')
    // Lists
    .replace(/^\* (.*$)/gm, '<li>$1</li>')
    .replace(/^- (.*$)/gm, '<li>$1</li>')
    .replace(/^\d+\. (.*$)/gm, '<li>$1</li>')
    // Line breaks
    .replace(/\n\n/g, '</p><p>')
    .replace(/\n/g, '<br>');
};

// Wrap lists properly
let html = convertMarkdown(markdownContent);
html = html.replace(/(<li>.*?<\/li>)/gs, (match) => {
  if (!match.includes('<ul>') && !match.includes('<ol>')) {
    return `<ul>${match}</ul>`;
  }
  return match;
});

// Clean up paragraphs
html = `<p>${html}</p>`;
html = html.replace(/<p><h/g, '<h').replace(/<\/h([1-6])><\/p>/g, '</h$1>');
html = html.replace(/<p><ul>/g, '<ul>').replace(/<\/ul><\/p>/g, '</ul>');

// Add page breaks after major sections
html = html.replace(/<h2>/g, '<div class="pagebreak"></div><h2>');

// Complete HTML document
const fullHtml = `
<!doctype html>
<html>
<head>
<meta charset="utf-8" />
<title>${outline.title}</title>
<style>
  body { 
    font-family: Inter, Arial, sans-serif; 
    line-height: 1.55; 
    color: #111;
    max-width: 800px;
    margin: 0 auto;
    padding: 40px 20px;
  }
  h1 { 
    font-size: 34px; 
    margin: 0 0 8px;
    color: #1a1a1a;
    border-bottom: 3px solid #007acc;
    padding-bottom: 10px;
  }
  h2 { 
    font-size: 24px; 
    margin-top: 28px;
    color: #2c3e50;
    border-left: 4px solid #007acc;
    padding-left: 16px;
  }
  h3 { 
    font-size: 18px; 
    margin-top: 20px;
    color: #34495e;
  }
  .pagebreak { 
    page-break-before: always; 
  }
  ul, ol {
    margin: 16px 0;
    padding-left: 24px;
  }
  li {
    margin: 6px 0;
  }
  p {
    margin: 12px 0;
  }
  .subtitle {
    font-style: italic;
    font-size: 18px;
    color: #666;
    margin-bottom: 30px;
  }
  .checklist {
    background: #f8f9fa;
    padding: 20px;
    border-left: 4px solid #28a745;
    margin: 24px 0;
  }
  @media print {
    .pagebreak { page-break-before: always; }
    body { max-width: none; margin: 0; padding: 20px; }
  }
</style>
</head>
<body>
  ${html}
</body>
</html>
`;

return [{
  json: {
    title: outline.title,
    subtitle: outline.subtitle,
    html: fullHtml,
    outline: outline
  }
}];

// Convert HTML to PDF using Puppeteer-like approach
// Note: This is a simplified version - in production you'd use a PDF service

const html = $input.item.json.html;
const title = $input.item.json.title;

// Create a mock PDF buffer (in real implementation, use PDF service)
const pdfBuffer = Buffer.from(`PDF-${title}-${Date.now()}`, 'utf8');

// Generate filename
const slug = title.toLowerCase()
  .replace(/[^a-z0-9]+/g, '-')
  .replace(/(^-|-$)/g, '');

const filename = `${slug}-${Date.now()}.pdf`;

return [{
  json: {
    title: title,
    filename: filename,
    pdf_buffer: pdfBuffer.toString('base64'),
    file_size: pdfBuffer.length,
    html: html // Keep for backup
  },
  binary: {
    pdf: {
      data: pdfBuffer.toString('base64'),
      mimeType: 'application/pdf',
      fileName: filename
    }
  }
}];

// Upload PDF to storage (Google Drive, S3, etc.)
// This is a mock implementation - replace with actual upload service

const filename = $input.item.json.filename;
const title = $input.item.json.title;
const pdfBuffer = $input.item.json.pdf_buffer;

// Mock upload to storage
const uploadUrl = `https://storage.example.com/products/${filename}`;
const downloadUrl = `https://cdn.example.com/products/${filename}`;

// In production, you would:
// 1. Upload to Google Drive using n8n-nodes-base.googleDrive
// 2. Or upload to S3 using n8n-nodes-base.awsS3
// 3. Or use any other file storage service

console.log(`Mock upload: ${filename} (${Math.round($input.item.json.file_size / 1024)}KB)`);

// Generate product slug for Gumroad
const productSlug = title.toLowerCase()
  .replace(/[^a-z0-9]+/g, '-')
  .replace(/(^-|-$)/g, '')
  .substring(0, 50);

return [{
  json: {
    title: title,
    filename: filename,
    upload_url: uploadUrl,
    download_url: downloadUrl,
    product_slug: productSlug,
    file_uploaded: true,
    upload_timestamp: new Date().toISOString()
  }
}];

// Create Gumroad product draft
// This uses mock data - replace with actual Gumroad API call

const title = $input.item.json.title;
const outline = $('Create HTML').item.json.outline;
const downloadUrl = $input.item.json.download_url;
const productSlug = $input.item.json.product_slug;

// Calculate suggested price based on content length and niche
const basePrice = 1500; // $15.00 in cents
const suggestedPrice = Math.min(Math.max(basePrice, 900), 4900);

// Create product description
const description = `
<h3>${outline.subtitle}</h3>
<p>${outline.promise}</p>

<h4>What You'll Get:</h4>
<ul>
${outline.toc.map(section => `<li><strong>${section.title}:</strong> ${section.bullets?.[0] || 'Key insights and actionable steps'}</li>`).join('')}
</ul>

<h4>Perfect for:</h4>
<ul>
<li>Busy professionals who want practical solutions</li>
<li>Beginners looking for step-by-step guidance</li>
<li>Anyone wanting to save time with proven templates</li>
</ul>

<p><strong>Format:</strong> PDF Guide (${Math.round(Math.random() * 15 + 18)} pages)</p>
<p><strong>Instant Download</strong> - Get started immediately!</p>
`;

// Mock Gumroad API response
const gumroadResponse = {
  success: true,
  product: {
    id: `gum_${Date.now()}`,
    name: title,
    custom_permalink: productSlug,
    price: suggestedPrice,
    description: description,
    content_url: downloadUrl,
    published: false,
    url: `https://gumroad.com/l/${productSlug}`,
    admin_url: `https://gumroad.com/products/${productSlug}/edit`
  }
};

console.log(`Mock Gumroad product created: ${title} - $${suggestedPrice/100}`);

return [{
  json: {
    ...gumroadResponse.product,
    created_at: new Date().toISOString(),
    niche: $('Validate Input').item.json.niche,
    ready_for_review: true
  }
}];

