const error = $input.item.json;

// Extract error details
const errorMessage = error.message || 'Unknown error';
const nodeName = error.node?.name || 'Unknown node';
const workflowName = error.workflow?.name || 'AI Newsletter Curator';
const executionId = error.execution?.id || 'N/A';
const timestamp = new Date().toISOString();

// Format error for Discord
return {
  json: {
    content: `❌ **Error in ${workflowName}**`,
    embeds: [{
      title: 'Workflow Error Alert',
      description: errorMessage,
      color: 15158332, // Red
      fields: [
        {
          name: 'Failed Node',
          value: nodeName,
          inline: true
        },
        {
          name: 'Execution ID',
          value: executionId,
          inline: true
        },
        {
          name: 'Error Type',
          value: error.name || 'ExecutionError',
          inline: true
        },
        {
          name: 'Stack Trace',
          value: (error.stack || 'No stack trace available').substring(0, 500) + '...',
          inline: false
        }
      ],
      timestamp: timestamp
    }]
  }
};

