    recentActivity: "Announced Series A funding"
  },
  {
    name: "Lisa Kumar",
    title: "CTO",
    company: "FinTech Forward",
    companySize: 300,
    industry: "Financial Technology",
    location: "Chicago, IL",
    profileUrl: "https://linkedin.com/in/lisakumar",
    email: null,
    recentActivity: "Looking for cloud migration solutions"
  }
];

// Add more mock data to reach maxItems
const expandedLeads = [];
for (let i = 0; i < Math.min(config.maxItems, 50); i++) {
  const baseLead = mockLeads[i % mockLeads.length];
  expandedLeads.push({
    ...baseLead,
    id: `lead_${Date.now()}_${i}`,
    discoveredAt: new Date().toISOString(),
    source: "LinkedIn Sales Navigator"
  });
}

return expandedLeads.map(lead => ({ json: lead }));

const lead = $input.item.json;
const config = $('Config').item.json;

// Check if lead matches criteria
const targetRoles = config.targetRoles.split(',').map(r => r.trim().toLowerCase());
const leadRole = lead.title.toLowerCase();
const roleMatch = targetRoles.some(role => leadRole.includes(role));

// Company size check
const sizeMatch = lead.companySize >= config.minCompanySize;

// Email enrichment simulation
if (!lead.email && !config.dryRun) {
  // In production, would use email finder API
  const firstName = lead.name.split(' ')[0].toLowerCase();
  const company = lead.company.toLowerCase().replace(/[^a-z0-9]/g, '');
  lead.email = `${firstName}@${company}.com`; // Simulated
  lead.emailConfidence = 0.75;
}

// Add qualification score
let score = 0;
if (roleMatch) score += 40;
if (sizeMatch) score += 30;
if (lead.recentActivity?.includes('hiring') || lead.recentActivity?.includes('funding')) score += 20;
if (lead.industry === 'Software Development' || lead.industry === 'AI/ML') score += 10;

lead.qualificationScore = score;
lead.qualified = score >= 60;
lead.matchCriteria = {
  roleMatch,
  sizeMatch,
  activitySignal: lead.recentActivity ? true : false
};

return { json: lead };

const config = $('Config').item.json;
const leads = $input.all();
const cacheKey = 'seen_lead_ids';

// Get existing cache
let seenLeads = $getWorkflowStaticData('global');
if (!seenLeads[cacheKey]) {
  seenLeads[cacheKey] = {};
}

// Filter out duplicates
const uniqueLeads = [];
const stats = {
  totalProcessed: leads.length,
  duplicates: 0,
  newLeads: 0
};

for (const lead of leads) {
  const leadKey = `${lead.json.name}_${lead.json.company}`.toLowerCase();

  if (!seenLeads[cacheKey][leadKey]) {
    uniqueLeads.push(lead);
    stats.newLeads++;

    if (!config.dryRun) {
      seenLeads[cacheKey][leadKey] = {
        firstSeen: new Date().toISOString(),
        profileUrl: lead.json.profileUrl
      };
    }
  } else {
    stats.duplicates++;
  }
}

// Clean old entries (keep last 10k)
if (Object.keys(seenLeads[cacheKey]).length > 10000) {
  const entries = Object.entries(seenLeads[cacheKey]);
  entries.sort((a, b) => new Date(a[1].firstSeen) - new Date(b[1].firstSeen));
  seenLeads[cacheKey] = Object.fromEntries(entries.slice(-5000));
}

// Add stats to each lead
return uniqueLeads.map(lead => ({
  json: {
    ...lead.json,
    stats
  }
}));

// Enhanced lead scoring with AI simulation
// In production, this would call Claude API for deeper analysis
const lead = $input.item.json;

// Simulate AI-powered insights
const aiInsights = {
  buyingIntent: 'medium',
  painPoints: [],
  recommendedApproach: '',
  estimatedDealSize: 0
};

// Analyze recent activity for buying signals
if (lead.recentActivity) {
  const activity = lead.recentActivity.toLowerCase();

  if (activity.includes('hiring') || activity.includes('scaling')) {
    aiInsights.buyingIntent = 'high';
    aiInsights.painPoints.push('scaling challenges');
  }

  if (activity.includes('funding') || activity.includes('series')) {
    aiInsights.buyingIntent = 'high';
    aiInsights.painPoints.push('rapid growth needs');
  }

  if (activity.includes('migration') || activity.includes('modernization')) {
    aiInsights.buyingIntent = 'high';
    aiInsights.painPoints.push('technical debt');
  }
}

// Estimate deal size based on company size
if (lead.companySize < 100) {
  aiInsights.estimatedDealSize = '$10K-50K';
  aiInsights.recommendedApproach = 'Self-serve with guided onboarding';
} else if (lead.companySize < 500) {
  aiInsights.estimatedDealSize = '$50K-200K';
  aiInsights.recommendedApproach = 'Inside sales with technical demo';
} else {
  aiInsights.estimatedDealSize = '$200K+';
  aiInsights.recommendedApproach = 'Enterprise sales with POC';
}

// Add industry-specific insights
if (lead.industry === 'Financial Technology') {
  aiInsights.painPoints.push('compliance requirements');
} else if (lead.industry === 'Healthcare') {
  aiInsights.painPoints.push('HIPAA compliance');
}

// Update qualification score with AI insights
if (aiInsights.buyingIntent === 'high') {
  lead.qualificationScore += 20;
}

lead.aiInsights = aiInsights;
lead.finalScore = Math.min(lead.qualificationScore, 100);
lead.tier = lead.finalScore >= 80 ? 'A' : lead.finalScore >= 60 ? 'B' : 'C';

return { json: lead };

const config = $('Config').item.json;
const leads = $input.all();
const stats = leads[0]?.json.stats || {};

// Group leads by tier
const tierA = leads.filter(l => l.json.tier === 'A');
const tierB = leads.filter(l => l.json.tier === 'B');
const tierC = leads.filter(l => l.json.tier === 'C');

// Format report
let reportData = {
  summary: {
    totalQualified: leads.length,
    tierA: tierA.length,
    tierB: tierB.length,
    tierC: tierC.length,
    ...stats
  },
  leads: []
};

// Format individual leads
leads.forEach(lead => {
  reportData.leads.push({
    name: lead.json.name,
    title: lead.json.title,
    company: lead.json.company,
    companySize: lead.json.companySize,
    location: lead.json.location,
    tier: lead.json.tier,
    score: lead.json.finalScore,
    buyingIntent: lead.json.aiInsights.buyingIntent,
    estimatedDealSize: lead.json.aiInsights.estimatedDealSize,
    recommendedApproach: lead.json.aiInsights.recommendedApproach,
    painPoints: lead.json.aiInsights.painPoints,
    profileUrl: lead.json.profileUrl,
    email: lead.json.email,
    emailConfidence: lead.json.emailConfidence
  });
});

// Create CSV format for export
const csvHeader = 'Name,Title,Company,Company Size,Location,Tier,Score,Buying Intent,Est. Deal Size,Profile URL\n';
const csvRows = reportData.leads.map(l => 
  `"${l.name}","${l.title}","${l.company}",${l.companySize},"${l.location}",${l.tier},${l.score},${l.buyingIntent},"${l.estimatedDealSize}","${l.profileUrl}"`
).join('\n');

reportData.csv = csvHeader + csvRows;
reportData.generatedAt = new Date().toISOString();
reportData.config = config;

return [{ json: reportData }];

const data = $input.item.json;
const config = data.config;

if (config.dryRun) {
  // Dry run - prepare preview
  return {
    json: {
      ...data,
      action: 'preview',
      message: 'Dry run - leads identified but not stored',
      previewLeads: data.leads.slice(0, 5), // Show first 5 leads
      deliveryMethod: 'Would be delivered via API/webhook to CRM'
    }
  };
} else {
  // Production - would send to CRM/database
  // Simulate API delivery
  return {
    json: {
      ...data,
      action: 'delivered',
      message: 'Leads delivered to CRM successfully',
      deliveredAt: new Date().toISOString(),
      deliveryMethod: 'API webhook',
      recordsCreated: data.leads.length
    }
  };
}

// Update workflow statistics
const config = $('Config').item.json;
const data = $input.item.json;

if (!config.dryRun) {
  const staticData = $getWorkflowStaticData('global');

  // Initialize stats if needed
  if (!staticData.leadStats) {
    staticData.leadStats = {
      totalLeadsGenerated: 0,
      totalTierA: 0,
      totalDelivered: 0,
      runCount: 0
    };
  }

  // Update cumulative stats
  staticData.leadStats.totalLeadsGenerated += data.summary.totalQualified;
  staticData.leadStats.totalTierA += data.summary.tierA;
  staticData.leadStats.totalDelivered += data.leads.length;
  staticData.leadStats.runCount += 1;
  staticData.leadStats.lastRun = new Date().toISOString();

  // Add lifetime stats to output
  data.lifetimeStats = staticData.leadStats;
}

return [{ json: data }];

