// Build target URLs for all data sources
const subreddits = $input.item.json.subreddits.split(',');
const gumroadQueries = $input.item.json.gumroad_queries.split(';');
const testMode = $input.item.json.test_mode;

// Limit targets in test mode
const finalSubreddits = testMode ? subreddits.slice(0, 1) : subreddits;
const finalQueries = testMode ? gumroadQueries.slice(0, 2) : gumroadQueries;

const targets = [];

// Reddit URLs
finalSubreddits.forEach(sub => {
  targets.push({
    kind: 'reddit',
    label: sub.trim(),
    url: `https://www.reddit.com/r/${sub.trim()}/top.json?t=week&limit=20`
  });
});

// Gumroad URLs
finalQueries.forEach(query => {
  const cleanQuery = query.trim();
  targets.push({
    kind: 'gumroad',
    label: cleanQuery,
    url: `https://gumroad.com/discover?query=${encodeURIComponent(cleanQuery)}`
  });

  // Also add Google Trends and Pinterest for same query
  targets.push({
    kind: 'trends',
    label: cleanQuery,
    url: `https://gtrends.mikko.vercel.app/api/trend/${encodeURIComponent(cleanQuery)}?geo=US`
  });

  targets.push({
    kind: 'pinterest',
    label: cleanQuery,
    url: `https://www.pinterest.com/search/pins/?q=${encodeURIComponent(cleanQuery)}`
  });
});

console.log(`Built ${targets.length} targets (test mode: ${testMode})`);

return targets.map(target => ({ json: target }));

// Normalize raw data into signals
const items = $input.all();
const signalsMap = {};

items.forEach(item => {
  const { kind, label } = item.json;
  const response = item.json.response || {};
  const body = response.body || '';

  if (!signalsMap[label]) {
    signalsMap[label] = {
      label,
      signals: {
        reddit_posts: 0,
        reddit_questions: 0,
        reddit_upvotes: 0,
        gum_count: 0,
        gum_avg_price: 0,
        trend_ratio: 1.0,
        pinterest_signal: 0
      }
    };
  }

  const signals = signalsMap[label].signals;

  try {
    switch (kind) {
      case 'reddit':
        if (typeof body === 'object' && body.data && body.data.children) {
          const posts = body.data.children;
          signals.reddit_posts = posts.length;

          let questions = 0;
          let upvotes = 0;

          posts.forEach(post => {
            const title = post.data.title || '';
            if (/\b(how|template|guide|planner)\b/i.test(title)) {
              questions++;
            }
            upvotes += post.data.score || 0;
          });

          signals.reddit_questions = questions;
          signals.reddit_upvotes = upvotes;
        }
        break;

      case 'gumroad':
        if (typeof body === 'string') {
          // Count product cards (rough heuristic)
          const productMatches = body.match(/product-card|product-title/g) || [];
          signals.gum_count = Math.min(productMatches.length, 100);

          // Extract prices
          const priceMatches = body.match(/\$([0-9]{1,3}(?:,[0-9]{3})*(?:\.[0-9]{2})?)/g) || [];
          if (priceMatches.length > 0) {
            const prices = priceMatches.map(p => parseInt(p.replace(/[$,]/g, '')));
            signals.gum_avg_price = Math.round(prices.reduce((a, b) => a + b, 0) / prices.length);
          }
        }
        break;

      case 'trends':
        if (typeof body === 'object' && body.timeline) {
          const timeline = body.timeline;
          if (timeline.length >= 12) {
            const last30 = timeline.slice(-1)[0]?.value || 50;
            const yearAvg = timeline.reduce((sum, point) => sum + (point.value || 0), 0) / timeline.length;
            signals.trend_ratio = yearAvg > 0 ? last30 / yearAvg : 1.0;
          }
        }
        break;

      case 'pinterest':
        if (typeof body === 'string') {
          // Crude proxy: HTML length as activity signal
          signals.pinterest_signal = Math.min(Math.round(body.length / 1000), 200);
        }
        break;
    }
  } catch (error) {
    console.log(`Error processing ${kind} for ${label}:`, error.message);
  }
});

return Object.values(signalsMap).map(item => ({ json: item }));

// Score each niche according to rubric
const items = $input.all();

if (!items || items.length === 0) {
  return [{ json: { error: 'No data to score', items: [] } }];
}

const scoredNiches = items.map(item => {
  const { label, signals } = item.json;

  // Scoring rubric (0-100 total)
  const demand = Math.min(signals.reddit_questions / 5, 1) * 20 + 
                Math.min(signals.reddit_upvotes / 500, 1) * 15;

  const monet = Math.min(signals.gum_count / 20, 1) * 10 + 
               Math.min(signals.gum_avg_price / 40, 1) * 15;

  const comp = (1 - Math.min(signals.gum_count / 30, 1)) * 20;

  const trend = Math.min(signals.trend_ratio / 1.3, 1) * 15 + 
               Math.min(signals.pinterest_signal / 50, 1) * 5;

  const total = Math.round(demand + monet + comp + trend);

  // Go/No-Go criteria
  const go = total >= 65 && 
            signals.gum_avg_price >= 9 && 
            signals.trend_ratio >= 0.9 && 
            signals.gum_count <= 45;

  return {
    json: {
      label,
      signals,
      scores: {
        demand: Math.round(demand),
        monet: Math.round(monet),
        comp: Math.round(comp),
        trend: Math.round(trend),
        total
      },
      go
    }
  };
});

// Sort by total score descending
scoredNiches.sort((a, b) => b.json.scores.total - a.json.scores.total);

return scoredNiches;

// Generate top 3 report and winner
const allNiches = $input.all();

if (!allNiches || allNiches.length === 0) {
  return [{
    json: {
      report: {
        top_3: [],
        winner: null,
        error: 'No niches data available'
      },
      metadata: {
        timestamp: new Date().toISOString(),
        targets: 0,
        groups: 0,
        top_label: 'none',
        winner_total: 0,
        degraded: true
      },
      allNiches: []
    }
  }];
}

const top3 = allNiches.slice(0, 3);

// Product ideas based on niche
const getProductIdea = (niche) => {
  const templates = [
    `Complete ${niche} Template Bundle`,
    `${niche} Checklist & Planning Kit`,
    `Ultimate ${niche} Guide with Templates`,
    `${niche} Workflow & Process Templates`,
    `${niche} Planner & Tracking System`
  ];
  return templates[Math.floor(Math.random() * templates.length)];
};

// Price suggestion based on scores
const suggestPrice = (scores) => {
  if (scores.total >= 80) return 29;
  if (scores.total >= 70) return 19;
  return 12;
};

const report = {
  top_3: top3.map(item => ({
    niche: item.json.label,
    why: `High ${item.json.scores.total}/100 score with ${item.json.signals.reddit_questions} questions and $${item.json.signals.gum_avg_price} avg price`,
    product_idea: getProductIdea(item.json.label),
    price_usd: suggestPrice(item.json.scores),
    go: item.json.go
  })),
  winner: null
};

// Set winner (first Go item or highest scoring)
const winner = top3.find(item => item.json.go) || top3[0];
if (winner) {
  // Generate seed keywords
  const baseKeywords = ['template', 'checklist', 'planner', 'guide', 'notion', 'excel', 'client', 'onboarding', 'bundle'];
  const nicheTokens = winner.json.label.toLowerCase().split(/[\s+]+/).filter(t => t.length > 2);
  const seedKeywords = [...new Set([...nicheTokens, ...baseKeywords])].slice(0, 12);

  report.winner = {
    niche: winner.json.label,
    seed_keywords: seedKeywords
  };
}

// Add metadata - Get timestamp from previous node with fallback
let timestamp;
try {
  timestamp = $('Initialize Configuration').item.json.timestamp;
} catch {
  timestamp = new Date().toISOString();
}

// Get targets count with fallback
let targetsCount = 0;
try {
  targetsCount = $('Build Target URLs').all().length;
} catch {
  targetsCount = 0;
}

const metadata = {
  timestamp: timestamp,
  targets: targetsCount,
  groups: allNiches.length,
  top_label: top3[0]?.json.label || 'none',
  winner_total: winner?.json.scores.total || 0,
  degraded: allNiches.some(item => 
    Object.values(item.json.signals).every(v => v === 0)
  )
};

return [{ json: { report, metadata, allNiches: allNiches.map(n => n.json) } }];

// Format Discord/Slack message
const data = $input.item.json;
const report = data.report;
const metadata = data.metadata;

if (!report.top_3 || report.top_3.length === 0) {
  return [{
    json: {
      message: `üéØ **Daily Niche Hunter Report** - ${metadata.timestamp.split('T')[0]}\n\n‚ùå **No niches found** - All data sources may be unavailable.\n\nTargets: ${metadata.targets} | Degraded: ${metadata.degraded}`
    }
  }];
}

let message = `üéØ **Daily Niche Hunter Report** - ${metadata.timestamp.split('T')[0]}\n\n**TOP 3 NICHES:**\n`;

report.top_3.forEach((item, index) => {
  const emoji = item.go ? '‚úÖ GO' : '‚è∏Ô∏è HOLD';
  message += `${index + 1}. **${item.niche}** (${emoji})\n`;
  message += `   ‚Ä¢ Product: ${item.product_idea}\n`;
  message += `   ‚Ä¢ Price: $${item.price_usd}\n`;
  message += `   ‚Ä¢ Why: ${item.why}\n\n`;
});

if (report.winner) {
  message += `**üèÜ WINNER:** ${report.winner.niche}\n`;
  message += `**Keywords:** ${report.winner.seed_keywords.join(', ')}\n\n`;
  message += `---\n**Handoff Payload:**\n\`\`\`json\n${JSON.stringify({niche: report.winner.niche, seed_keywords: report.winner.seed_keywords}, null, 2)}\n\`\`\``;
} else {
  message += `**No winner selected** - No niches met Go criteria.`;
}

return [{ json: { message } }];

